        pagedItems = Items?.AsEnumerable()?.Skip((page - 1) * itemsPerPage).Take(itemsPerPage).ToList();
    }

    private void OnPageChanged(int page)
    {
        UpdatePagedItems(page);
    }
    private IEnumerable<TItem> FilteredItems => FilterItems();
